mapper.select:
SELECT


	,case
	when user_contact_apply.contact_type = 0 then u.nick_name
	when user_contact_apply.contact_type = 1 then g.group_name
	end as contactName

FROM user_contact_apply

	LEFT JOIN user_info u on user_contact_apply.apply_user_id = u.user_id and user_contact_apply.receive_user_id = #{query.receiveUserId}
	LEFT JOIN group_info g on user_contact_apply.contact_id = g.group_id and user_contact_apply.receive_user_id = #{query.receiveUserId}



	order by ${query.orderBy}


	limit #{query.simplePage.start},#{query.simplePage.end}
